import request from '@/utils/request'

// 获取帖子列表
export function getPostList(params) {
  console.log(`开始请求帖子列表API，参数:`, params);

  // 创建临时数据，用于后端API失败时显示
  const tempPosts = [
    {
      id: 1,
      title: "后端API暂时不可用 - 临时显示内容",
      content: "这是临时生成的内容，因为后端API返回了500错误。请联系管理员检查服务器日志。",
      created_at: new Date().toISOString(),
      views: 0,
      likes_count: 0,
      comments_count: 0,
      is_pinned: false,
      is_featured: false,
      board_name: '系统通知',
      user: {
        id: 0,
        username: 'system',
        nickname: '系统通知',
        avatar_url: ''
      }
    }
  ];

  return request({
    url: '/api/v1/posts/',
    method: 'get',
    params
  }).then(response => {
    console.log(`帖子列表API响应成功:`, response);

    // 检查响应是否包含HTML错误页面（表示服务器500错误）
    if (response && typeof response.data === 'string' && 
        (response.data.includes('<!DOCTYPE html>') || response.data.includes('Error'))) {
      console.error('服务器返回了HTML错误页面，显示临时数据');
      return {
        code: 500,
        status: 500,
        message: '服务器错误，请稍后再试',
        data: { 
          results: tempPosts,
          count: tempPosts.length
        }
      };
    }

    // 如果响应成功且有数据，直接返回
    if (response && response.code === 0 && response.data && 
        (Array.isArray(response.data) || 
         (response.data.results && Array.isArray(response.data.results)))) {
      return response;
    }

    // 如果响应本身就是数据对象或数组
    if (response && (Array.isArray(response) || 
                    (response.results && Array.isArray(response.results)))) {
      return {
        code: 0,
        status: 0,
        data: response
      };
    }

    // 如果响应不成功或没有数据，返回临时数据
    console.log('帖子列表API返回异常或空数据，显示临时数据');
    return {
      code: response?.code || 404,
      status: response?.status || 404,
      message: '后端API暂时不可用，显示临时数据',
      data: { 
        results: tempPosts,
        count: tempPosts.length
      }
    };
  }).catch(error => {
    console.error('获取帖子列表失败，显示临时数据', error);
    // 返回临时数据
    return {
      code: error?.response?.status || 500,
      status: error?.response?.status || 500,
      message: error?.message || '网络错误，请稍后再试',
      data: { 
        results: tempPosts,
        count: tempPosts.length
      }
    };
  });
}

// 获取帖子详情
export function getPostDetail(id) {
  console.log(`开始请求帖子详情API，ID: ${id}`);

  // 创建临时帖子数据，用于后端API失败时显示
  const tempPost = {
    id: id || 1,
    title: "后端API暂时不可用 - 临时显示内容",
    content: `<p>这是临时生成的帖子内容，因为后端API返回了错误。</p>
              <p>请联系管理员检查服务器日志，查看ContentNotRenderedError错误。</p>
              <p>这可能是Django序列化响应时出现的问题。</p>`,
    created_at: new Date().toISOString(),
    updated_at: new Date().toISOString(),
    views: 0,
    likes_count: 0,
    comments_count: 0,
    is_pinned: false,
    is_featured: false,
    board: { id: 1, name: '系统通知' },
    user: {
      id: 0,
      username: 'system',
      nickname: '系统通知',
      avatar_url: ''
    }
  };

  return request({
    url: `/api/v1/posts/${id}/`,
    method: 'get'
  }).then(response => {
    console.log(`帖子详情API响应成功:`, response);

    // 检查响应是否包含HTML错误页面（表示服务器500错误）
    if (response && typeof response.data === 'string' && 
        (response.data.includes('<!DOCTYPE html>') || response.data.includes('Error'))) {
      console.error('服务器返回了HTML错误页面，显示临时数据');
      return {
        code: 500,
        status: 500,
        message: '服务器错误，请稍后再试',
        data: tempPost
      };
    }

    // 如果响应成功且有数据，直接返回
    if (response && response.data && 
        (typeof response.data === 'object' && Object.keys(response.data).length > 0)) {
      return {
        code: 0,
        status: 0,
        data: response.data
      };
    } else if (response && typeof response === 'object' && 
               Object.keys(response).length > 0 && 
               !response.data) {
      // 如果响应本身就是数据对象
      return {
        code: 0,
        status: 0,
        data: response
      };
    }

    // 如果响应不成功或没有数据，返回临时数据
    console.log('帖子详情API返回异常或空数据，显示临时数据');
    return {
      code: 404,
      status: 404,
      message: '未找到帖子，显示临时内容',
      data: tempPost
    };
  }).catch(error => {
    console.error(`获取帖子详情(ID: ${id})失败，显示临时数据`, error);
    // 返回临时数据
    return {
      code: error?.response?.status || 500,
      status: error?.response?.status || 500,
      message: error?.message || '网络错误，请稍后再试',
      data: tempPost
    };
  });
}

// 创建帖子
export function createPost(data) {
  return request({
    url: '/api/v1/posts/',
    method: 'post',
    data
  })
}

// 更新帖子
export function updatePost(id, data) {
  return request({
    url: `/api/v1/posts/${id}/`,
    method: 'put',
    data
  })
}

