        code: 500,
        status: 500,
        message: '服务器错误，请稍后再试',
        data: tempComments
      };
    }

    // 如果响应成功且有数据，直接返回
    if (response && response.data && 
        (Array.isArray(response.data) || 
         (typeof response.data === 'object' && response.data.results))) {
      // 处理不同格式的响应
      const comments = Array.isArray(response.data) ? 
        response.data : 
        (response.data.results || []);

      return {
        code: 0,
        status: 0,
        data: comments
      };
    } else if (response && typeof response === 'object' && 
               Array.isArray(response)) {
      // 如果响应本身就是数组
      return {
        code: 0,
        status: 0,
        data: response
      };
    }

    // 如果响应不成功或没有数据，返回临时数据
    console.log('帖子评论API返回异常或空数据，显示临时数据');
    return {
      code: 404,
      status: 404,
      message: '未找到评论，显示临时内容',
      data: tempComments
    };
  }).catch(error => {
    console.error(`获取帖子评论(帖子ID: ${postId})失败，显示临时数据`, error);
    // 返回临时数据
    return {
      code: error?.response?.status || 500,
      status: error?.response?.status || 500,
      message: error?.message || '网络错误，请稍后再试',
      data: tempComments
    };
  });
}
