/**
 * Quill编辑器全局配置
 * 用于解决Quill中的警告和配置问题
 */

// 默认的Quill配置选项
export const defaultQuillOptions = {
  theme: 'snow',
  modules: {
    toolbar: [
      ['bold', 'italic', 'underline', 'strike'],
      ['blockquote', 'code-block'],
      [{ 'header': 1 }, { 'header': 2 }],
      [{ 'list': 'ordered' }, { 'list': 'bullet' }],
      [{ 'script': 'sub' }, { 'script': 'super' }],
      [{ 'indent': '-1' }, { 'indent': '+1' }],
      [{ 'direction': 'rtl' }],
      [{ 'size': ['small', false, 'large', 'huge'] }],
      [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
      [{ 'color': [] }, { 'background': [] }],
      [{ 'font': [] }],
      [{ 'align': [] }],
      ['clean'],
      ['link', 'image']
    ]
  },
  placeholder: '请输入内容...',
  scrollingContainer: 'html',
  bounds: document.body,
  // 使用 MutationObserver 替代已弃用的 DOM 事件
  experimental: {
    useModernScrollingApi: true,
    useMutationObserver: true
  }
};

// 初始化Quill，应用全局配置
export function initQuill(Quill) {
  // 这里可以添加Quill的全局配置和扩展
  // 例如注册自定义格式、模块等
  
  // 禁用控制台警告
  if (process.env.NODE_ENV === 'production') {
    console.warn = (function(originalWarn) {
      return function(msg, ...args) {
        // 过滤掉Quill的特定警告
        if (msg && typeof msg === 'string' && 
            (msg.includes('DOMNodeInserted') || 
             msg.includes('Listener added for a') ||
             msg.includes('mutation event'))) {
          return;
        }
        originalWarn.call(console, msg, ...args);
      };
    })(console.warn);
  }
  
  return Quill;
}
