import axios from 'axios'
import router from '../router'
import { ElMessage } from 'element-plus'

// 创建axios实例
const service = axios.create({
  baseURL: 'http://localhost:8000',
  timeout: 10000
})

// 请求拦截器
service.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token')
    console.log('发送请求:', config.url, '是否有token:', !!token)

    // 设置Authorization头
    if (token) {
      // 确保token格式正确
      const formattedToken = token.startsWith('Bearer ') ? token : `Bearer ${token}`
      config.headers.Authorization = formattedToken
      console.log('设置Authorization头:', formattedToken)
    } else {
      console.log('没有token，不设置Authorization头')
    }

    // 设置Content-Type头（如果没有设置）
    if (config.method === 'post' || config.method === 'put' || config.method === 'patch') {
      if (!config.headers['Content-Type'] && !(config.data instanceof FormData)) {
        config.headers['Content-Type'] = 'application/json'
        console.log('设置Content-Type头: application/json')
      }
    }

    // 对于热门板块API，特别处理
    if (config.url.includes('/api/v1/boards/hot/')) {
      console.log('热门板块API请求，确保正确设置参数:', config.params)
    }

    console.log('请求头部:', config.headers)
    return config
  },
  error => {
    console.error('请求错误:', error)
    return Promise.reject(error)
  }
)

// 响应拦截器
service.interceptors.response.use(
  response => {
    const res = response.data

    // 检查是否有明确的错误状态
    if (res.status && res.status !== 0 && res.status !== 200) {
      ElMessage({
        message: res.msg || '请求失败',
        type: 'error',
        duration: 5 * 1000
      })

      // 401: 未登录或token过期
      if (res.status === 401) {
        // 清除本地token和用户信息
        localStorage.removeItem('token')
        localStorage.removeItem('userInfo')

        // 跳转到登录页
        router.push('/login')
      }

      return Promise.reject(new Error(res.msg || '请求失败'))
    } else {
      // 返回整个响应数据，让调用者决定如何处理
      // 不自动显示成功消息，由具体组件决定是否显示

      // 如果是“操作成功”消息，则不显示
      if (res.msg === '操作成功') {
        console.log('拦截到“操作成功”消息，不显示')
        // 清除所有消息，确保不显示“操作成功”
        ElMessage.closeAll()

        // 修改响应中的消息，避免其他地方显示
        res.msg = ''
      }

      // 如果当前路径是发帖页面，则清除所有消息
      const currentPath = window.location.pathname
      if (currentPath.includes('/post/create') || currentPath.includes('/post/edit')) {
        console.log('当前路径是发帖页面，清除所有消息')
        ElMessage.closeAll()
      }

      return res
    }
  },
  error => {
    console.error('响应错误:', error)

    // 处理HTTP错误状态码
    if (error.response) {
      const status = error.response.status
      console.log('HTTP错误状态码:', status)
      console.log('错误响应数据:', error.response.data)

      if (status === 401) {
        ElMessage({
          message: '登录已过期，请重新登录',
          type: 'error',
          duration: 5 * 1000
        })

        // 清除本地token和用户信息
        localStorage.removeItem('token')
        localStorage.removeItem('userInfo')

        // 跳转到登录页
        router.push('/login')
      } else if (status === 500) {
        // 对于500错误，特别是热门板块API，提供更详细的错误信息
        if (error.config && error.config.url && error.config.url.includes('/api/v1/boards/hot/')) {
          console.error('热门板块API 500错误:', error.response.data)

          // 返回一个成功的响应，但使用空数据，避免前端崩溃
          return {
            status: 0,
            msg: '',
            data: []
          }
        }

        ElMessage({
          message: '服务器内部错误，请稍后再试',
          type: 'error',
          duration: 5 * 1000
        })
      } else {
        ElMessage({
          message: error.response.data.msg || '请求失败',
          type: 'error',
          duration: 5 * 1000
        })
      }
    } else if (error.request) {
      // 请求已发送但没有收到响应
      console.error('请求已发送但没有收到响应:', error.request)
      ElMessage({
        message: '服务器无响应，请检查服务器是否正常运行',
        type: 'error',
        duration: 5 * 1000
      })
    } else {
      // 请求配置有误
      console.error('请求配置有误:', error.message)
      ElMessage({
        message: '网络错误，请检查您的网络连接',
        type: 'error',
        duration: 5 * 1000
      })
    }

    return Promise.reject(error)
  }
)

export default service
