import axios from 'axios'
import router from '../router'
import { ElMessage } from 'element-plus'

// 增强的消息去重机制
const messageTracker = {
  lastMessage: '',
  lastTime: 0,
  messageQueue: [],
  showMessage(message, type, source = 'unknown') {
    const now = Date.now();
    const messageInfo = { message, type, time: now, source };

    console.log(`消息触发 [${source}]: ${message} (${type})`, messageInfo);

    // 如果相同消息在3秒内出现，则不重复显示
    if (message === this.lastMessage && now - this.lastTime < 3000) {
      console.log(`消息被去重: ${message} (上次显示时间: ${new Date(this.lastTime).toLocaleTimeString()})`);
      return;
    }

    // 清理过期的消息
    this.messageQueue = this.messageQueue.filter(msg => now - msg.time < 5000);

    // 检查队列中是否已有相同消息
    const duplicate = this.messageQueue.find(msg => msg.message === message);
    if (duplicate) {
      console.log(`队列中已存在相同消息: ${message} (来源: ${duplicate.source})`);
      return;
    }

    // 添加到消息队列
    this.messageQueue.push(messageInfo);
    this.lastMessage = message;
    this.lastTime = now;

    console.log(`显示消息: ${message}`);
    ElMessage.closeAll(); // 关闭所有现有消息
    ElMessage({
      message: message,
      type: type,
      duration: type === 'error' ? 5 * 1000 : 3000,
      onClose: () => {
        // 从队列中移除
        this.messageQueue = this.messageQueue.filter(msg => msg.message !== message);
      }
    });
  }
};

// 全局挂载消息追踪器，用于其他组件直接使用
window.messageTracker = messageTracker;

// 创建自定义消息显示函数，替代直接调用ElMessage
const showSafeMessage = (options) => {
  if (typeof options === 'string') {
    messageTracker.showMessage(options, 'info', 'safe-message');
  } else {
    messageTracker.showMessage(
      options.message, 
      options.type || 'info', 
      'safe-message-' + (options.type || 'info')
    );
  }
};

// 全局暴露，方便其他组件使用
window.showSafeMessage = showSafeMessage;

// 创建axios实例
const service = axios.create({
  baseURL: 'http://localhost:8000',
  timeout: 10000,
  // 添加validateStatus选项，允许所有状态码通过，不抛出请求错误
  validateStatus: function (status) {
    return status < 600 // 所有HTTP状态码都作为成功处理，包括500错误
  }
})

// 请求拦截器
service.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token')

    // 设置Authorization头
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
    }

    // 设置Content-Type头（如果没有设置）
    if (config.method === 'post' || config.method === 'put' || config.method === 'patch') {
      if (!config.headers['Content-Type'] && !(config.data instanceof FormData)) {
        config.headers['Content-Type'] = 'application/json'
      }
    }

    return config
  },
  error => {
    console.error('请求错误:', error)
    return Promise.reject(error)
  }
)

// 辅助函数：判断是否为登出操作
function isLogoutOperation(config) {
  if (!config || !config.url || !config.method) {
    return false
  }

  const isPostMethod = config.method.toLowerCase() === 'post'
  const isLogoutUrl = config.url.includes('/api/v1/auth/logout/')

  console.log('请求拦截器检测登出操作:', {
    url: config.url,
    method: config.method,
    isLogoutUrl,
    isPostMethod,
    isLogout: isLogoutUrl && isPostMethod
  })

  return isLogoutUrl && isPostMethod
}

// 辅助函数：判断是否为公开内容请求（无需登录即可访问）
function isPublicContentRequest(config) {
  if (!config || !config.url || !config.method) {
    return false
  }

  const isGetMethod = config.method.toLowerCase() === 'get'
  const isPostsUrl = config.url.includes('/api/v1/posts')
  const isBoardsUrl = config.url.includes('/api/v1/boards')
  const isRankingUrl = config.url.includes('/api/v1/ranking')

  console.log('请求拦截器检测公开内容请求:', {
    url: config.url,
    method: config.method,
    isGetMethod,
    isPostsUrl,
    isBoardsUrl,
    isRankingUrl,
    isPublicContent: isGetMethod && (isPostsUrl || isBoardsUrl || isRankingUrl)
  })

  // GET 请求 + 公开API路径 = 公开内容请求
  return isGetMethod && (isPostsUrl || isBoardsUrl || isRankingUrl)
}

// 响应拦截器
service.interceptors.response.use(
  response => {
    const res = response.data
    console.log('响应拦截器收到响应:', res)

    // 检查是否有明确的错误状态
    if ((res.status !== undefined && res.status !== 0 && res.status !== 200) || 
        (res.code !== undefined && res.code !== 0 && res.code !== 200)) {
      console.error('API错误响应:', res)
      messageTracker.showMessage(res.msg || '请求失败', 'error', 'axios-response-error');

      // 401: 未登录或token过期
      if ((res.status === 401 || res.code === 401) && !isPublicContentRequest(response.config)) {
        // 对于公开内容，即使返回401也不跳转登录页
        // 清除本地token和用户信息
        localStorage.removeItem('token')
        localStorage.removeItem('userInfo')

        // 跳转到登录页
        router.push('/login')
      }

      return Promise.reject(new Error(res.msg || '请求失败'))
    } else {
      // 如果是"操作成功"消息，且不在特定页面，且不是登出操作，才显示成功消息
      if (res.msg && res.msg !== '操作成功' && !shouldSuppressMessage() && 
          !isCommentDeleteOperation(response.config) && 
          !isPostOperation(response.config) &&
          !isLogoutOperation(response.config)) {
        messageTracker.showMessage(res.msg, 'success', 'axios-response-success');
      }

      // 兼容不同的响应格式
      if (res.code === undefined && res.status === undefined) {
        // 如果响应中既没有code也没有status字段，可能是直接返回的数据
        return {
          code: 0,
          status: 0,
          msg: '',
          data: res
        }
      }

      return res
    }
  },
  error => {
    console.error('响应错误:', error)

    // 处理HTTP错误状态码
    if (error.response) {
      const status = error.response.status

      // 对于公开内容请求，不自动跳转到登录页
      if (status === 401 && !isPublicContentRequest(error.config)) {
        messageTracker.showMessage('登录已过期，请重新登录', 'error', 'axios-401');

        // 清除本地token和用户信息
        localStorage.removeItem('token')
        localStorage.removeItem('userInfo')

        // 跳转到登录页
        router.push('/login')
      } else if (status === 404) {
        messageTracker.showMessage('请求的资源不存在', 'error', 'axios-404');
      } else if (status === 500) {
        messageTracker.showMessage('服务器内部错误', 'error', 'axios-500');
      } else {
        // 尝试从响应中获取错误信息
        let errorMsg = '请求失败'
        try {
          if (error.response.data) {
            if (error.response.data.msg) {
              errorMsg = error.response.data.msg
            } else if (error.response.data.detail) {
              errorMsg = error.response.data.detail
            } else if (typeof error.response.data === 'string') {
              errorMsg = error.response.data
            }
          }
        } catch (e) {
          console.error('解析错误信息失败:', e)
        }

        messageTracker.showMessage(errorMsg, 'error', 'axios-other-error');
      }
    } else if (error.request) {
      // 请求已发送但没有收到响应
      messageTracker.showMessage('服务器无响应，请检查网络连接', 'error', 'axios-no-response');
    } else {
      // 请求配置有误
      messageTracker.showMessage('网络错误，请检查网络连接', 'error', 'axios-config-error');
    }

    return Promise.reject(error)
  }
)

// 辅助函数：判断当前页面是否应该抑制消息显示
function shouldSuppressMessage() {
  const currentPath = window.location.pathname
  return currentPath.includes('/post/create') || 
         currentPath.includes('/post/edit') || 
         (currentPath.includes('/post/') && !currentPath.includes('/create') && !currentPath.includes('/edit'))
}

// 辅助函数：判断是否为评论删除操作
function isCommentDeleteOperation(config) {
  if (!config || !config.url || !config.method) {
    console.log('无法判断是否为评论删除操作，配置不完整:', config)
    return false
  }

  const isDeleteMethod = config.method.toLowerCase() === 'delete'
  const isCommentUrl = config.url.includes('/api/v1/comments/')

  console.log('请求拦截器检测:', {
    url: config.url,
    method: config.method,
    isDeleteMethod,
    isCommentUrl,
    isCommentDelete: isDeleteMethod && isCommentUrl
  })

  return isCommentUrl && isDeleteMethod
}

// 辅助函数：判断是否为帖子相关操作
function isPostOperation(config) {
  if (!config || !config.url || !config.method) {
    return false
  }

  // 帖子URL模式
  const isPostUrl = config.url.includes('/api/v1/posts/')

  // 判断是否为编辑、点赞、收藏、置顶、加精等操作
  const isPutMethod = config.method.toLowerCase() === 'put'
  const isPostMethod = config.method.toLowerCase() === 'post'

  // 特定操作的URL模式
  const isLikeUrl = config.url.includes('/like')
  const isFavoriteUrl = config.url.includes('/favorite')
  const isPinUrl = config.url.includes('/pin')
  const isFeatureUrl = config.url.includes('/feature')

  // 判断是否为帖子编辑或特定操作
  const isPostEdit = isPutMethod && isPostUrl && !isLikeUrl && !isFavoriteUrl && !isPinUrl && !isFeatureUrl
  const isPostAction = isPostUrl && (isLikeUrl || isFavoriteUrl || isPinUrl || isFeatureUrl)
  const isPostCreate = isPostMethod && config.url === '/api/v1/posts/'

  console.log('请求拦截器检测帖子操作:', {
    url: config.url,
    method: config.method,
    isPostUrl,
    isPutMethod,
    isPostMethod,
    isLikeUrl,
    isFavoriteUrl,
    isPinUrl,
    isFeatureUrl,
    isPostEdit,
    isPostAction,
    isPostCreate,
    isPostOperation: isPostEdit || isPostAction || isPostCreate
  })

