// 在最开始设置 Quill 的默认配置
if (typeof window !== 'undefined') {
  window.Quill = window.Quill || {};
  window.Quill.DEFAULTS = window.Quill.DEFAULTS || {};
  window.Quill.DEFAULTS.experimental = {
    useModernScrollingApi: true,
    useMutationObserver: true
  };
}

import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import store from './store'
import ElementPlus from 'element-plus'
import 'element-plus/dist/index.css'
import service from './utils/request'
import UserAvatar from './components/UserAvatar.vue'
import BackButton from './components/BackButton.vue'
import { QuillEditor } from '@vueup/vue-quill'
import '@vueup/vue-quill/dist/vue-quill.snow.css'
import { initQuill } from './utils/quill-config'
import webSocketManager from './utils/websocket'

// 在导入Quill之前设置警告拦截器
const originalWarn = console.warn;
console.warn = function(msg, ...args) {
  if (msg && typeof msg === 'string' && 
      (msg.includes('DOMNodeInserted') || 
       msg.includes('Listener added for a') ||
       msg.includes('mutation event'))) {
    // 忽略Quill相关警告
    return;
  }
  originalWarn.call(console, msg, ...args);
};

// 初始化 Quill
initQuill();

const app = createApp(App)

app.use(store)
app.use(router)
app.use(ElementPlus)

// 全局挂载axios
app.config.globalProperties.$axios = service

// 全局注册组件
app.component('UserAvatar', UserAvatar)
app.component('BackButton', BackButton)
app.component('QuillEditor', QuillEditor)

// 初始化WebSocket
const initWebSockets = () => {
  // 检查是否已登录
  const token = store.state.token
  if (token) {
    // 请求桌面通知权限
    if (Notification && Notification.permission !== 'granted' && Notification.permission !== 'denied') {
      Notification.requestPermission()
    }

    // 初始化通知WebSocket
    webSocketManager.initNotificationSocket()
  }
}

// 在路由准备就绪后初始化WebSocket
router.isReady().then(() => {
  // 挂载应用
  app.mount('#app')

  // 初始化WebSocket
  initWebSockets()
})
