import axios from 'axios'
import router from '../router'
import { ElMessage } from 'element-plus'

// 创建axios实例
const service = axios.create({
  baseURL: 'http://localhost:8000',
  timeout: 10000
})

// 请求拦截器
service.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token')
    console.log('发送请求:', config.url, '是否有token:', !!token)

    // 设置Authorization头
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
    }

    // 设置Content-Type头（如果没有设置）
    if (config.method === 'post' || config.method === 'put' || config.method === 'patch') {
      if (!config.headers['Content-Type'] && !(config.data instanceof FormData)) {
        config.headers['Content-Type'] = 'application/json'
      }
    }

    console.log('请求头部:', config.headers)
    return config
  },
  error => {
    console.error('请求错误:', error)
    return Promise.reject(error)
  }
)

// 响应拦截器
service.interceptors.response.use(
  response => {
    const res = response.data
    console.log('响应数据:', res)

    // 检查是否有明确的错误状态
    if (res.status && res.status !== 0 && res.status !== 200) {
      console.error('API错误响应:', res)
      ElMessage({
        message: res.msg || '请求失败',
        type: 'error',
        duration: 5 * 1000
      })

      // 401: 未登录或token过期
      if (res.status === 401) {
        console.log('检测到401未授权状态，准备清除token并跳转到登录页')
        // 清除本地token和用户信息
        localStorage.removeItem('token')
        localStorage.removeItem('userInfo')

        // 跳转到登录页
        router.push('/login')
      }

      return Promise.reject(new Error(res.msg || '请求失败'))
    } else {
      // 返回整个响应数据，让调用者决定如何处理
      // 不自动显示成功消息，由具体组件决定是否显示

      // 如果是“操作成功”消息，则不显示
      if (res.msg === '操作成功') {
        console.log('拦截到“操作成功”消息，不显示')
        // 清除所有消息，确保不显示“操作成功”
        ElMessage.closeAll()

        // 修改响应中的消息，避免其他地方显示
        res.msg = ''
      }

      // 如果当前路径是发帖页面，则清除所有消息
      const currentPath = window.location.pathname
      if (currentPath.includes('/post/create') || currentPath.includes('/post/edit')) {
        console.log('当前路径是发帖页面，清除所有消息')
        ElMessage.closeAll()
      }

      // 兼容不同的响应格式
      if (res.code === undefined && res.status === undefined) {
        // 如果响应中既没有code也没有status字段，可能是直接返回的数据
        console.log('检测到直接返回的数据格式，进行标准化处理')
        return {
          code: 0,
          status: 0,
          msg: '',
          data: res
        }
      }

      return res
    }
  },
  error => {
    console.error('响应错误:', error)

    // 处理HTTP错误状态码
    if (error.response) {
      const status = error.response.status
      console.error('HTTP错误状态码:', status, '响应数据:', error.response.data)

      if (status === 401) {
        ElMessage({
          message: '登录已过期，请重新登录',
          type: 'error',
          duration: 5 * 1000
        })

        // 清除本地token和用户信息
        localStorage.removeItem('token')
        localStorage.removeItem('userInfo')

        // 跳转到登录页
        router.push('/login')
      } else if (status === 404) {
        ElMessage({
          message: '请求的资源不存在，请检查API路径是否正确',
          type: 'error',
          duration: 5 * 1000
        })
      } else if (status === 500) {
        ElMessage({
          message: '服务器内部错误，请联系管理员',
          type: 'error',
          duration: 5 * 1000
        })
      } else {
        // 尝试从响应中获取错误信息
        let errorMsg = '请求失败'
        try {
          if (error.response.data) {
            if (error.response.data.msg) {
              errorMsg = error.response.data.msg
            } else if (error.response.data.detail) {
              errorMsg = error.response.data.detail
            } else if (typeof error.response.data === 'string') {
              errorMsg = error.response.data
            }
          }
        } catch (e) {
          console.error('解析错误信息失败:', e)
        }

        ElMessage({
          message: errorMsg,
          type: 'error',
          duration: 5 * 1000
        })
      }
    } else if (error.request) {
      // 请求已发送但没有收到响应
      console.error('请求已发送但没有收到响应:', error.request)
      ElMessage({
        message: '服务器无响应，请检查服务器是否正常运行',
        type: 'error',
        duration: 5 * 1000
      })
    } else {
      // 请求配置有误
      console.error('请求配置有误:', error.message)
      ElMessage({
        message: '网络错误，请检查您的网络连接',
        type: 'error',
        duration: 5 * 1000
      })
    }

    return Promise.reject(error)
  }
)

export default service
