import ReconnectingWebSocket from 'reconnecting-websocket';
import store from '@/store';
import { ElMessage } from 'element-plus';

// WebSocket基础URL
const WS_BASE_URL = process.env.NODE_ENV === 'production' 
  ? 'wss://yourserver.com/ws' 
  : 'ws://localhost:8001/ws';

/**
 * WebSocket管理类
 */
class WebSocketManager {
  constructor() {
    this.notificationSocket = null;
    this.chatSockets = {}; // 存储与不同用户的聊天WebSocket连接
    this.token = null;
  }

  /**
   * 初始化WebSocket连接
   * @param {string} token - JWT令牌
   */
  init(token) {
    this.token = token;
    if (!token) {
      console.log('没有令牌，尝试从localStorage获取');
      this.token = localStorage.getItem('token');
      if (!this.token) {
        console.log('无法获取令牌，不建立WebSocket连接');
        return;
      }
    }

    // 检查后端是否支持通知功能
    fetch('http://localhost:8001/api/v1/notifications/', {
      headers: {
        'Authorization': `Bearer ${this.token}`
      }
    })
      .then(response => {
        // 如果通知接口存在，则连接WebSocket
        if (response.status !== 404) {
          this.connectNotification(this.token);
        } else {
          console.log('通知接口不可用，不建立WebSocket连接');
        }
      })
      .catch(error => {
        console.error('检查通知接口可用性失败:', error);
      });
  }

  /**
   * 连接到通知WebSocket
   * @param {string} token - JWT令牌
   */
  connectNotification(token) {
    if (this.notificationSocket) {
      this.notificationSocket.close();
      this.notificationSocket = null;
    }

    // 如果已经禁用了WebSocket功能，则不再尝试连接
    if (window.webSocketEnabled === false) {
      console.log('WebSocket功能已被禁用，不再尝试连接');
      return;
    }

    try {
    // 创建通知WebSocket连接
      this.notificationSocket = new ReconnectingWebSocket(`${WS_BASE_URL}/notifications/?token=${token}`, [], {
        connectionTimeout: 3000,
        maxRetries: 3,  // 最多重试3次
        debug: false
      });

    // 连接打开时的处理
    this.notificationSocket.onopen = () => {
      console.log('通知WebSocket连接已建立');
    };

    // 接收消息的处理
    this.notificationSocket.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        switch (data.type) {
          case 'connection_established':
            console.log(data.message);
            break;

          case 'unread_count':
            // 更新未读通知数量
            store.commit('notifications/setUnreadCount', data.count);
            break;

          case 'notification':
            // 处理新通知
            store.commit('notifications/addNotification', data);
            store.commit('notifications/incrementUnreadCount');

            // 显示通知提示
            ElMessage({
              message: data.title,
              type: 'info',
              duration: 3000
            });
            break;

          case 'mark_read_response':
          case 'mark_all_read_response':
            // 标记通知已读的响应处理
            break;

          default:
            console.log('未知的通知类型:', data.type);
        }
      } catch (error) {
        console.error('解析通知消息失败:', error);
      }
    };

    // 连接关闭时的处理
    this.notificationSocket.onclose = () => {
      console.log('通知WebSocket连接已关闭');
    };

    // 连接错误时的处理
    this.notificationSocket.onerror = (error) => {
      console.error('通知WebSocket连接错误:', error);
        // 连接错误时，设置全局标志，避免反复尝试连接
        window.webSocketEnabled = false;
    };
    } catch (error) {
      console.error('创建通知WebSocket连接失败:', error);
    }
  }

  /**
   * 连接到聊天WebSocket
   * @param {number} userId - 对方用户ID
   */
  connectChat(userId) {
    if (!this.token) {
      console.error('未登录，无法建立聊天连接');
      return;
    }

    // 如果已存在与该用户的连接，则返回
    if (this.chatSockets[userId]) {
      return;
    }

    // 从Bearer token中提取实际的token
    const rawToken = this.token.replace('Bearer ', '');

    // 创建聊天WebSocket连接，使用URL参数传递token
    const chatSocket = new ReconnectingWebSocket(
      `${WS_BASE_URL}/chat/${userId}?token=${rawToken}`,
      [],
      {
        connectionTimeout: 5000,
        maxRetries: 10,
        maxReconnectionDelay: 5000,
        minReconnectionDelay: 1000,
        debug: true,
        // 添加WebSocket选项
        wsOptions: {
          origin: window.location.origin
        }
      }
    );

    // 连接打开时的处理
    chatSocket.onopen = () => {
      console.log(`与用户${userId}的聊天WebSocket连接已建立`);
    };

    // 接收消息的处理
    chatSocket.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        switch (data.type) {
          case 'connection_established':
            console.log(data.message);
            break;

          case 'chat_message':
            // 处理聊天消息，添加去重逻辑
            if (!store.getters['chat/hasMessage'](data.id)) {
              store.commit('chat/addMessage', {
                userId: data.sender_id === store.state.user.currentUser.id ? data.receiver_id : data.sender_id,
                message: {
                  id: data.id,
                  senderId: data.sender_id,
                  receiverId: data.receiver_id,
                  content: data.content,
                  timestamp: data.timestamp,
                  isRead: data.is_read
                }
              });
            }
            break;

          case 'history_messages':
            // 处理历史消息
            store.commit('chat/setMessages', {
              userId: userId,
              messages: data.messages.map(msg => ({
                id: msg.id,
                senderId: msg.sender_id,
                receiverId: msg.receiver_id,
                content: msg.content,
                timestamp: msg.timestamp,
                isRead: msg.is_read
              })),
              hasMore: data.has_more,
              page: data.page
            });
            break;

          default:
            console.log('未知的聊天消息类型:', data.type);
        }
      } catch (error) {
        console.error('解析聊天消息失败:', error);
      }
    };

    // 连接关闭时的处理
    chatSocket.onclose = (event) => {
      console.log(`与用户${userId}的聊天WebSocket连接已关闭`);
      delete this.chatSockets[userId];

      // 处理特定的关闭代码
      switch (event.code) {
        case 4001:
          console.error('WebSocket连接失败：未提供token');
          break;
        case 4002:
          console.error('WebSocket连接失败：用户不存在');
          break;
        case 4003:
          console.error('WebSocket连接失败：token无效');
          // 可能需要触发重新登录
          store.dispatch('auth/logout');
          break;
        default:
          if (event.code !== 1000) {  // 1000是正常关闭
            console.error(`WebSocket连接异常关闭，代码：${event.code}`);
          }
      }
    };

    // 连接错误时的处理
    chatSocket.onerror = (error) => {
      console.error(`与用户${userId}的聊天WebSocket连接错误:`, error);
    };

    // 保存连接
    this.chatSockets[userId] = chatSocket;
  }

  /**
   * 断开与用户的聊天连接
   * @param {number} userId - 用户ID
   */
  disconnectChat(userId) {
    if (this.chatSockets[userId]) {
      this.chatSockets[userId].close();
      delete this.chatSockets[userId];
    }
  }

  /**
   * 发送聊天消息
   * @param {number} userId - 接收者ID
   * @param {string} content - 消息内容
   */
  sendChatMessage(userId, content) {
    if (!this.chatSockets[userId]) {
      this.connectChat(userId);
    }

    this.chatSockets[userId].send(JSON.stringify({
      type: 'chat_message',
      content: content
    }));
  }

  /**
   * 加载历史消息
   * @param {number} userId - 用户ID
   * @param {number} page - 页码
   * @param {number} pageSize - 每页大小
   */
  loadChatHistory(userId, page = 1, pageSize = 20) {
    if (!this.chatSockets[userId]) {
      this.connectChat(userId);
    }

    this.chatSockets[userId].send(JSON.stringify({
      type: 'load_history',
      page: page,
      page_size: pageSize
    }));
  }

  /**
   * 标记通知为已读
   * @param {number} notificationId - 通知ID
   */
  markNotificationRead(notificationId) {
    if (this.notificationSocket && this.notificationSocket.readyState === WebSocket.OPEN) {
      this.notificationSocket.send(JSON.stringify({
        type: 'mark_read',
        id: notificationId
      }));
    }
  }

  /**
   * 标记所有通知为已读
   */
  markAllNotificationsRead() {
    if (this.notificationSocket && this.notificationSocket.readyState === WebSocket.OPEN) {
      this.notificationSocket.send(JSON.stringify({
        type: 'mark_all_read'
      }));
    }
  }

  /**
   * 关闭所有WebSocket连接
   */
  closeAll() {
    if (this.notificationSocket) {
      this.notificationSocket.close();
      this.notificationSocket = null;
    }

    Object.values(this.chatSockets).forEach(socket => {
      socket.close();
    });
    this.chatSockets = {};
    this.token = null;
  }
}

// 创建单例
const wsManager = new WebSocketManager();

export default wsManager;
