import { createStore } from 'vuex'

export default createStore({
  state: {
    token: localStorage.getItem('token') || '',
    userInfo: JSON.parse(localStorage.getItem('userInfo')) || null,
    isAuthenticated: !!localStorage.getItem('token')
  },
  getters: {
    isAuthenticated: state => {
      const hasToken = !!localStorage.getItem('token')
      console.log('获取登录状态:', state.isAuthenticated, '本地存储token:', hasToken)
      // 确保状态与本地存储一致
      if (state.isAuthenticated !== hasToken) {
        console.warn('状态与本地存储不一致，以本地存储为准')
        state.isAuthenticated = hasToken
      }
      return state.isAuthenticated
    },
    userInfo: state => state.userInfo,
    token: state => state.token,
    userId: state => state.userInfo?.id,
    isAdmin: state => {
      // 检查用户是否是管理员
      return state.userInfo && (
        state.userInfo.is_superuser ||
        state.userInfo.role === 'admin' ||
        state.userInfo.is_admin
      )
    }
  },
  mutations: {
    SET_TOKEN(state, token) {
      console.log('设置token:', token)
      state.token = token
      state.isAuthenticated = !!token
      localStorage.setItem('token', token)
    },
    SET_USER_INFO(state, userInfo) {
      console.log('设置用户信息:', userInfo)
      state.userInfo = userInfo
      localStorage.setItem('userInfo', JSON.stringify(userInfo))
    },
    CLEAR_AUTH(state) {
      console.log('清除登录状态')
      state.token = ''
      state.userInfo = null
      state.isAuthenticated = false
      localStorage.removeItem('token')
      localStorage.removeItem('userInfo')
    }
  },
  actions: {
    login({ commit }, { token, userInfo }) {
      console.log('登录操作, token:', token, '用户信息:', userInfo)
      commit('SET_TOKEN', token)
      commit('SET_USER_INFO', userInfo)
    },
    logout({ commit }) {
      console.log('登出操作')
      commit('CLEAR_AUTH')
    },
    updateUserInfo({ commit }, userInfo) {
      console.log('更新用户信息:', userInfo)
      commit('SET_USER_INFO', userInfo)
    }
  },
  modules: {
  }
})
