import request from '@/utils/request'

// 获取板块列表
export function getBoardList() {
  return request({
    url: '/api/v1/boards/',
    method: 'get'
  })
}

// 获取板块详情
export function getBoardDetail(id) {
  return request({
    url: `/api/v1/boards/${id}/`,
    method: 'get'
  })
}

// 获取热门板块
export function getHotBoards(limit = 5) {
  console.log('调用getHotBoards API，limit:', limit)
  return request({
    url: '/api/v1/boards/hot/',
    method: 'get',
    params: { limit },
    // 增加超时时间，避免网络问题导致请求失败
    timeout: 15000
  }).catch(error => {
    console.error('获取热门板块API错误:', error)
    // 返回一个默认的成功响应，避免前端崩溃
    return {
      status: 0,
      msg: '',
      data: []
    }
  })
}

// 创建板块 (管理员)
export function createBoard(data) {
  return request({
    url: '/api/v1/boards/',
    method: 'post',
    data
  })
}

// 更新板块 (管理员)
export function updateBoard(id, data) {
  return request({
    url: `/api/v1/boards/${id}/`,
    method: 'put',
    data
  })
}

// 删除板块 (管理员)
export function deleteBoard(id) {
  return request({
    url: `/api/v1/boards/${id}/`,
    method: 'delete'
  })
}

// 重新排序板块 (管理员)
export function reorderBoards(data) {
  return request({
    url: '/api/v1/boards/reorder/',
    method: 'post',
    data
  })
}

// 获取所有用户组 (管理员)
export function getGroups() {
  return request({
    url: '/api/v1/boards/groups/',
    method: 'get'
  })
}

// 获取所有用户 (管理员)
export function getUsers() {
  return request({
    url: '/api/v1/boards/users/',
    method: 'get'
  })
}
