import request from '@/utils/request'

// 模拟帖子数据，用于API调用失败时返回
export const mockPosts = [
  {
    id: 1,
    title: '欢迎使用论坛系统',
    content: '这是一个基于Vue和Django的论坛系统',
    created_at: new Date().toISOString(),
    views: 100,
    likes_count: 50,
    comments_count: 20,
    is_pinned: true,
    is_featured: false,
    board_name: '公告板块',
    user: {
      id: 1,
      username: 'admin',
      nickname: '管理员',
      avatar_url: ''
    }
  },
  {
    id: 2,
    title: '如何使用论坛的功能',
    content: '本帖介绍论坛的基本功能和使用方法',
    created_at: new Date(Date.now() - 86400000).toISOString(), // 一天前
    views: 85,
    likes_count: 30,
    comments_count: 15,
    is_pinned: false,
    is_featured: true,
    board_name: '使用指南',
    user: {
      id: 2,
      username: 'guide',
      nickname: '指南员',
      avatar_url: ''
    }
  },
  {
    id: 3,
    title: '分享一些有用的网站资源',
    content: '这里收集了一些学习编程的好网站',
    created_at: new Date(Date.now() - 172800000).toISOString(), // 两天前
    views: 65,
    likes_count: 25,
    comments_count: 10,
    is_pinned: false,
    is_featured: false,
    board_name: '资源分享',
    user: {
      id: 3,
      username: 'resource',
      nickname: '资源达人',
      avatar_url: ''
    }
  }
]

// 生成模拟帖子详情数据
function getMockPostDetail(id) {
  return {
    id: id || 1,
    title: "模拟帖子标题 - 服务器暂时不可用",
    content: "这是一个模拟帖子内容。当前服务器API不可用，我们显示的是模拟数据。请稍后再试。\n\n这个帖子是由前端自动生成的，用于在后端API不可用时提供基本的浏览体验。",
    author: {
      id: 1,
      username: "系统用户",
      avatar: null
    },
    created_at: new Date().toISOString(),
    updated_at: new Date().toISOString(),
    view_count: 0,
    like_count: 0,
    comment_count: 0,
    tags: ["系统", "模拟数据"],
    is_mock: true  // 标记这是模拟数据
  };
}

// 获取帖子列表
export function getPostList(params) {
  console.log(`开始请求帖子列表API，参数:`, params);

  return request({
    url: '/api/v1/posts/',
    method: 'get',
    params
  }).then(response => {
    console.log(`帖子列表API响应成功:`, response);

    // 检查响应是否包含HTML错误页面（表示服务器500错误）
    if (response && typeof response.data === 'string' && 
        (response.data.includes('<!DOCTYPE html>') || response.data.includes('Error'))) {
      console.error('服务器返回了HTML错误页面');
      return {
        code: 500,
        status: 500,
        message: '服务器错误，请稍后再试',
        data: { results: [], count: 0 }
      };
    }

    // 如果响应成功且有数据，直接返回
    if (response && response.code === 0 && response.data && 
        (Array.isArray(response.data) || 
         (response.data.results && Array.isArray(response.data.results)))) {
      return response;
    }

    // 如果响应本身就是数据对象或数组
    if (response && (Array.isArray(response) || 
                    (response.results && Array.isArray(response.results)))) {
      return {
        code: 0,
        status: 0,
        data: response
      };
    }

    // 如果响应不成功或没有数据，返回空数组
    console.log('帖子列表API返回异常或空数据');
    return {
      code: response?.code || 404,
      status: response?.status || 404,
      message: '未找到数据',
      data: { results: [], count: 0 }
    };
  }).catch(error => {
    console.error('获取帖子列表失败', error);
    // 返回错误信息
    return {
      code: error?.response?.status || 500,
      status: error?.response?.status || 500,
      message: error?.message || '网络错误，请稍后再试',
      data: { results: [], count: 0 }
    };
  });
}

// 获取帖子详情
export function getPostDetail(id) {
  console.log(`开始请求帖子详情API，ID: ${id}`);

  return request({
    url: `/api/v1/posts/${id}/`,
    method: 'get'
  }).then(response => {
    console.log(`帖子详情API响应成功:`, response);

    // 检查响应是否包含HTML错误页面（表示服务器500错误）
    if (response && typeof response.data === 'string' && 
        (response.data.includes('<!DOCTYPE html>') || response.data.includes('Error'))) {
      console.error('服务器返回了HTML错误页面');
      return {
        code: 500,
        status: 500,
        message: '服务器错误，请稍后再试',
        data: null
      };
    }

    // 如果响应成功且有数据，直接返回
    if (response && response.data && 
        (typeof response.data === 'object' && Object.keys(response.data).length > 0)) {
      return {
        code: 0,
        status: 0,
        data: response.data
      };
    } else if (response && typeof response === 'object' && 
               Object.keys(response).length > 0 && 
               !response.data) {
      // 如果响应本身就是数据对象
      return {
        code: 0,
        status: 0,
        data: response
      };
    }

    // 如果响应不成功或没有数据，返回错误信息
    console.log('帖子详情API返回异常或空数据');
    return {
      code: 404,
      status: 404,
      message: '未找到帖子',
      data: null
    };
  }).catch(error => {
    console.error(`获取帖子详情(ID: ${id})失败`, error);
    // 返回错误信息
    return {
      code: error?.response?.status || 500,
      status: error?.response?.status || 500,
      message: error?.message || '网络错误，请稍后再试',
      data: null
    };
  });
}

// 创建帖子
export function createPost(data) {
  return request({
    url: '/api/v1/posts/',
    method: 'post',
    data
  })
}

// 更新帖子
export function updatePost(id, data) {
  return request({
    url: `/api/v1/posts/${id}/`,
    method: 'put',
    data
  })
}

// 删除帖子
export function deletePost(id) {
  return request({
    url: `/api/v1/posts/${id}/`,
    method: 'delete'
  })
}

// 点赞/取消点赞帖子
export function likePost(id) {
  return request({
    url: `/api/v1/posts/${id}/like/`,
    method: 'post'
  })
}

// 收藏/取消收藏帖子
export function favoritePost(id) {
  return request({
    url: `/api/v1/posts/${id}/favorite/`,
    method: 'post'
  })
}

// 置顶/取消置顶帖子 (管理员)
export function pinPost(id) {
  return request({
    url: `/api/v1/posts/${id}/pin/`,
    method: 'put'
  })
}

// 加精/取消加精帖子 (管理员)
export function featurePost(id) {
  return request({
    url: `/api/v1/posts/${id}/feature/`,
    method: 'put'
  })
}

// 获取用户收藏的帖子列表
export function getUserFavorites() {
  return request({
    url: '/api/v1/posts/favorites/',
    method: 'get'
  })
}

// 获取帖子评论
export function getPostComments(postId) {
  console.log(`开始请求帖子评论API，帖子ID: ${postId}`);

  return request({
    url: `/api/v1/posts/${postId}/comments/`,
    method: 'get'
  }).then(response => {
    console.log(`帖子评论API响应成功:`, response);

    // 检查响应是否包含HTML错误页面（表示服务器500错误）
    if (response && typeof response.data === 'string' && 
        (response.data.includes('<!DOCTYPE html>') || response.data.includes('Error'))) {
      console.error('服务器返回了HTML错误页面');
      return {
        code: 500,
        status: 500,
        message: '服务器错误，请稍后再试',
        data: []
      };
    }

    // 如果响应成功且有数据，直接返回
    if (response && response.data && 
        (Array.isArray(response.data) || 
         (typeof response.data === 'object' && response.data.results))) {
      // 处理不同格式的响应
      const comments = Array.isArray(response.data) ? 
        response.data : 
        (response.data.results || []);

      return {
        code: 0,
        status: 0,
        data: comments
      };
    } else if (response && typeof response === 'object' && 
               Array.isArray(response)) {
      // 如果响应本身就是数组
      return {
        code: 0,
        status: 0,
        data: response
      };
    }

    // 如果响应不成功或没有数据，返回空数组
    console.log('帖子评论API返回异常或空数据');
    return {
      code: 404,
      status: 404,
      message: '未找到评论',
      data: []
    };
  }).catch(error => {
    console.error(`获取帖子评论(帖子ID: ${postId})失败`, error);
    // 返回错误信息
    return {
      code: error?.response?.status || 500,
      status: error?.response?.status || 500,
      message: error?.message || '网络错误，请稍后再试',
      data: []
    };
  });
}
