import request from '@/utils/request'

// 获取评论列表
export function getCommentList(params) {
  return request({
    url: '/api/v1/comments/',
    method: 'get',
    params
  })
}

// 获取帖子的评论列表
export function getPostComments(postId) {
  return request({
    url: '/api/v1/comments/',
    method: 'get',
    params: {
      post: postId,
      parent: null // 只获取顶级评论
    }
  })
}

// 获取评论详情
export function getCommentDetail(id) {
  return request({
    url: `/api/v1/comments/${id}/`,
    method: 'get'
  })
}

// 创建评论
export function createComment(data) {
  return request({
    url: '/api/v1/comments/',
    method: 'post',
    data
  })
}

// 回复评论
export function replyComment(postId, parentId, content) {
  return request({
    url: '/api/v1/comments/',
    method: 'post',
    data: {
      post: postId,
      parent: parentId,
      content
    }
  })
}

// 更新评论
export function updateComment(id, data) {
  return request({
    url: `/api/v1/comments/${id}/`,
    method: 'put',
    data
  })
}

// 删除评论
export function deleteComment(id) {
  return request({
    url: `/api/v1/comments/${id}/`,
    method: 'delete'
  })
}
