import { createRouter, createWebHistory } from 'vue-router'

// 路由懒加载
const Home = () => import('../views/Home.vue')
const Login = () => import('../views/Login.vue')
const Register = () => import('../views/Register.vue')
// 不再需要单独的邮箱验证注册页面
const ResetPassword = () => import('../views/ResetPassword.vue')
const NotFound = () => import('../views/NotFound.vue')

// 板块相关页面
const BoardList = () => import('../views/board/BoardList.vue')
const BoardDetail = () => import('../views/board/BoardDetail.vue')

// 帖子相关页面
const PostList = () => import('../views/post/PostList.vue')
const PostDetail = () => import('../views/post/PostDetail.vue')
const PostCreate = () => import('../views/post/PostCreate.vue')

// 用户中心相关页面
const UserProfile = () => import('../views/user/Profile.vue')
const ChangePassword = () => import('../views/user/ChangePassword.vue')

// 管理员相关页面
const AdminDashboard = () => import('../views/admin/Dashboard.vue')
const UserManagement = () => import('../views/admin/UserManagement.vue')

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home,
    meta: {
      title: '首页 - 论坛系统'
    }
  },
  {
    path: '/login',
    name: 'Login',
    component: Login,
    meta: {
      title: '登录 - 论坛系统'
    }
  },
  {
    path: '/register',
    name: 'Register',
    component: Register,
    meta: {
      title: '注册 - 论坛系统'
    }
  },
  // 移除单独的邮箱验证注册路由，直接使用主注册页面
  {
    path: '/reset-password',
    name: 'ResetPassword',
    component: ResetPassword,
    meta: {
      title: '重置密码 - 论坛系统'
    }
  },
  // 板块相关路由
  {
    path: '/boards',
    name: 'BoardList',
    component: BoardList,
    meta: {
      title: '板块列表 - 论坛系统'
    }
  },
  {
    path: '/boards/:id',
    name: 'BoardDetail',
    component: BoardDetail,
    meta: {
      title: '板块详情 - 论坛系统'
    }
  },
  // 帖子相关路由
  {
    path: '/posts',
    name: 'PostList',
    component: PostList,
    meta: {
      title: '帖子列表 - 论坛系统'
    }
  },
  {
    path: '/posts/:id',
    name: 'PostDetail',
    component: PostDetail,
    meta: {
      title: '帖子详情 - 论坛系统'
    }
  },
  {
    path: '/post/create',
    name: 'PostCreate',
    component: PostCreate,
    meta: {
      title: '发布帖子 - 论坛系统',
      requiresAuth: true
    }
  },
  {
    path: '/post/edit/:id',
    name: 'PostEdit',
    component: PostCreate,
    meta: {
      title: '编辑帖子 - 论坛系统',
      requiresAuth: true
    }
  },
  // 用户中心相关路由
  {
    path: '/profile/:id',
    name: 'UserProfile',
    component: UserProfile,
    meta: {
      title: '用户中心 - 论坛系统'
    }
  },
  {
    path: '/profile/password',
    name: 'ChangePassword',
    component: ChangePassword,
    meta: {
      title: '修改密码 - 论坛系统',
      requiresAuth: true
    }
  },
  {
    path: '/:pathMatch(.*)*',
    name: 'NotFound',
    component: NotFound,
    meta: {
      title: '页面未找到 - 论坛系统'
    }
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

// 全局前置守卫，设置页面标题和权限检查
router.beforeEach((to, from, next) => {
  // 设置页面标题
  document.title = to.meta.title || '论坛系统'

  // 检查是否需要登录
  const requiresAuth = to.matched.some(record => record.meta.requiresAuth)
  const isLoggedIn = localStorage.getItem('token')

  if (requiresAuth && !isLoggedIn) {
    next({
      path: '/login',
      query: { redirect: to.fullPath }
    })
  } else {
    next()
  }
})

export default router
