import { createStore } from 'vuex'
import axios from '@/utils/request'

export default createStore({
  state: {
    token: localStorage.getItem('token') || '',
    user: JSON.parse(localStorage.getItem('user') || '{}'),
    // 通知相关状态
    notifications: [],
    unreadNotificationCount: 0,
    // 聊天相关状态
    chatMessages: {}, // { userId: [messages] }
    unreadMessageCounts: {}, // { userId: count }
    currentChatUserId: null,
    chatHistoryPages: {}, // { userId: { page, hasMore } }
  },
  getters: {
    isLoggedIn: state => !!state.token,
    isAdmin: state => state.user && state.user.role === 'admin',
    isModerator: state => state.user && (state.user.role === 'moderator' || state.user.role === 'admin'),
    userId: state => state.user ? state.user.id : null,
    username: state => state.user ? state.user.username : '',
    userAvatar: state => state.user && state.user.avatar_url ? state.user.avatar_url : '',
    userRole: state => state.user ? state.user.role : '',
    totalUnreadMessageCount: state => {
      return Object.values(state.unreadMessageCounts).reduce((total, count) => total + count, 0);
    },
    getUserById: () => () => {
      // 这里应该从用户缓存中获取，目前简单返回null
      // 实际应用中应该维护一个用户缓存
      return null;
    },
    getChatMessages: state => userId => {
      return state.chatMessages[userId] || [];
    },
    getUnreadMessageCount: state => userId => {
      return state.unreadMessageCounts[userId] || 0;
    },
    getChatHistoryPage: state => userId => {
      return state.chatHistoryPages[userId] || { page: 1, hasMore: true };
    }
  },
  mutations: {
    // 用户认证相关
    setToken(state, token) {
      state.token = token;
      localStorage.setItem('token', token);
    },
    setUser(state, user) {
      state.user = user;
      localStorage.setItem('user', JSON.stringify(user));
    },
    logout(state) {
      state.token = '';
      state.user = {};
      localStorage.removeItem('token');
      localStorage.removeItem('user');
    },

    // 通知相关
    setNotifications(state, notifications) {
      state.notifications = notifications;
    },
    addNotification(state, notification) {
      state.notifications.unshift(notification);
    },
    setUnreadNotificationCount(state, count) {
      state.unreadNotificationCount = count;
    },
    incrementUnreadNotificationCount(state) {
      state.unreadNotificationCount++;
    },
    markNotificationRead(state, notificationId) {
      const notification = state.notifications.find(n => n.id === notificationId);
      if (notification) {
        notification.is_read = true;
        if (state.unreadNotificationCount > 0) {
          state.unreadNotificationCount--;
        }
      }
    },
    markAllNotificationsRead(state) {
      state.notifications.forEach(notification => {
        notification.is_read = true;
      });
      state.unreadNotificationCount = 0;
    },

    // 聊天相关
    setCurrentChatUserId(state, userId) {
      state.currentChatUserId = userId;
      // 当切换到某个用户的聊天时，重置该用户的未读消息计数
      if (userId) {
        state.unreadMessageCounts[userId] = 0;
      }
    },
    initChatMessages(state, userId) {
      if (!state.chatMessages[userId]) {
        state.chatMessages[userId] = [];
      }
    },
    setChatMessages(state, { userId, messages }) {
      state.chatMessages[userId] = messages;
    },
    addChatMessage(state, { userId, message }) {
      if (!state.chatMessages[userId]) {
        state.chatMessages[userId] = [];
      }
      state.chatMessages[userId].unshift(message);
    },
    setChatHistory(state, { userId, messages, hasMore, page }) {
      if (!state.chatMessages[userId]) {
        state.chatMessages[userId] = [];
      }

      // 如果是第一页，替换所有消息
      if (page === 1) {
        state.chatMessages[userId] = messages;
      } else {
        // 否则追加消息
        state.chatMessages[userId] = [...state.chatMessages[userId], ...messages];
      }

      // 更新分页信息
      state.chatHistoryPages[userId] = { page, hasMore };
    },
    setUnreadMessageCount(state, { userId, count }) {
      state.unreadMessageCounts = { ...state.unreadMessageCounts, [userId]: count };
    },
    incrementUnreadMessageCount(state, userId) {
      const currentCount = state.unreadMessageCounts[userId] || 0;
      state.unreadMessageCounts = { ...state.unreadMessageCounts, [userId]: currentCount + 1 };
    },
    resetUnreadMessageCount(state, userId) {
      state.unreadMessageCounts = { ...state.unreadMessageCounts, [userId]: 0 };
    }
  },
  actions: {
    // 用户认证相关
    login({ commit }, credentials) {
      return axios.post('/api/v1/auth/login/', credentials)
        .then(response => {
          const { token, user } = response.data.data;
          commit('setToken', token);
          commit('setUser', user);
          return user;
        });
    },
    logout({ commit }) {
      commit('logout');
    },

    // 通知相关
    fetchNotifications({ commit }) {
      return axios.get('/api/v1/notifications/')
        .then(response => {
          commit('setNotifications', response.data.data.results);
          return response.data.data.results;
        });
    },
    fetchUnreadNotificationCount({ commit }) {
      return axios.get('/api/v1/notifications/unread/count/')
        .then(response => {
          commit('setUnreadNotificationCount', response.data.data.count);
          return response.data.data.count;
        });
    },
    markNotificationRead({ commit }, notificationId) {
      return axios.put(`/api/v1/notifications/${notificationId}/read/`)
        .then(() => {
          commit('markNotificationRead', notificationId);
        });
    },
    markAllNotificationsRead({ commit }) {
      return axios.put('/api/v1/notifications/read/all/')
        .then(() => {
          commit('markAllNotificationsRead');
        });
    },

    // 聊天相关
    fetchChatMessages({ commit }, userId) {
      commit('initChatMessages', userId);
      return axios.get(`/api/v1/user-notifications/messages/with_user/`, {
        params: { user_id: userId, page: 1, page_size: 20 }
      })
        .then(response => {
          commit('setChatMessages', { 
            userId, 
            messages: response.data.messages.map(msg => ({
              id: msg.id,
              senderId: msg.sender.id,
              receiverId: msg.receiver.id,
              content: msg.content,
              timestamp: msg.created_at,
              isRead: msg.is_read,
              isOutgoing: msg.sender.id === this.state.user.id
            }))
          });
          return response.data.messages;
        });
    },
    fetchUnreadMessageCount({ commit }, userId) {
      // 这个API可能需要后端实现
      return axios.get(`/api/v1/user-notifications/messages/unread/count/`, {
        params: { user_id: userId }
      })
        .then(response => {
          commit('setUnreadMessageCount', { userId, count: response.data.count });
          return response.data.count;
        });
    },
    sendChatMessage({ commit }, { userId, content }) {
      return axios.post('/api/v1/user-notifications/messages/send/', {
        receiver_id: userId,
        content: content
      })
        .then(response => {
          const message = {
            id: response.data.id,
            senderId: response.data.sender.id,
            receiverId: response.data.receiver.id,
            content: response.data.content,
            timestamp: response.data.created_at,
            isRead: response.data.is_read,
            isOutgoing: true
          };
          commit('addChatMessage', { userId, message });
          return message;
        });
    },
    markChatMessagesRead({ commit }, userId) {
      return axios.post('/api/v1/user-notifications/messages/mark_read/', {
        user_id: userId
      })
        .then(() => {
          commit('resetUnreadMessageCount', userId);
        });
    }
  },
  modules: {
  }
})
