import { createRouter, createWebHistory } from 'vue-router'

// 路由懒加载
const Home = () => import('../views/Home.vue')
const Login = () => import('../views/Login.vue')
const Register = () => import('../views/Register.vue')
// 不再需要单独的邮箱验证注册页面
const ResetPassword = () => import('../views/ResetPassword.vue')
const NotFound = () => import('../views/NotFound.vue')

// 板块相关页面
const BoardList = () => import('../views/board/BoardList.vue')
const BoardDetail = () => import('../views/board/BoardDetail.vue')

// 帖子相关页面
const PostList = () => import('../views/post/PostList.vue')
const PostDetail = () => import('../views/post/PostDetail.vue')
const PostCreate = () => import('../views/post/PostCreate.vue')

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home,
    meta: {
      title: '首页 - 论坛系统'
    }
  },
  {
    path: '/login',
    name: 'Login',
    component: Login,
    meta: {
      title: '登录 - 论坛系统'
    }
  },
  {
    path: '/register',
    name: 'Register',
    component: Register,
    meta: {
      title: '注册 - 论坛系统'
    }
  },
  // 移除单独的邮箱验证注册路由，直接使用主注册页面
  {
    path: '/reset-password',
    name: 'ResetPassword',
    component: ResetPassword,
    meta: {
      title: '重置密码 - 论坛系统'
    }
  },
  {
    path: '/:pathMatch(.*)*',
    name: 'NotFound',
    component: NotFound,
    meta: {
      title: '页面未找到 - 论坛系统'
    }
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

// 全局前置守卫，设置页面标题
router.beforeEach((to, from, next) => {
  document.title = to.meta.title || '论坛系统'
  next()
})

export default router
